  padding: 0px 0.5rem;
  border: 1px solid #ff4800;
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border-radius: 5px;
  margin-top: 1rem;
  cursor: pointer;
}
.select-games-content .select-games-section .col-md-4 .select-game-box .selec-game-box-inside input[type=submit]:hover {
  background-color: #ff4800;
  color: #fff;
}
